import React, { useContext } from "react";
import Select from "react-select";
import options from "../context/countries";
import { UserContext } from "../context/core";

export const MainPage = () => {
  const {
    SearchCovid,
    handleChange,
    handleCity,
    searchHotel,
    todayCase,

    cities,

    city,

    hotels,

    weather,
  } = useContext(UserContext);

  return (
    <>
      <Select options={options} onChange={handleChange.bind(this)} />

      <button onClick={SearchCovid}>Search</button>
      <h1>Today's Case: {todayCase}</h1>

      <div>
        {cities.map((city, index) => (
          <span key={city.name}>
            <div onClick={() => handleCity(index)}>{city.name}</div>
          </span>
        ))}
      </div>
      <button onClick={searchHotel}>SearchHotel</button>
      <div>
        {hotels.map((hotel, index) => (
          <span key={hotel.name}>
            <div>{hotel.name}</div>
          </span>
        ))}
      </div>
      <div>
        temp in {city}: {weather} celcius
      </div>
    </>
  );
};

export default MainPage;
